        if ($data['paramArray'][5]['value'] == 'true' || $data['paramArray'][5]['value'] == true || $data['paramArray'][5]['value'] == 1) {

            switch ($data['paramArray'][6]['value']) {
                case 'без НДС':
                    $tax = 1;
                    break;
                case '0%':
                    $tax = 2;
                    break;
                case '10%':
                    $tax = 5;
                    break;
                
                default:
                    $tax = 6;
                    break;
            }

            if (isset($data['orderInfo'][$data['id']]['user_email'])) {
                $customerContact = $data['orderInfo'][$data['id']]['user_email'];
            }
            else if (isset($data['orderInfo'][$data['id']]['phone'])) {
                $customerContact = $data['orderInfo'][$data['id']]['phone'];
                $customerContact = preg_replace("/[^0-9+]/", '', $customerContact);
            }

            $content = unserialize(stripslashes($data['orderInfo'][$data['id']]['order_content']));

            $ym_merchant_receipt = array(
                "customerContact" => $customerContact,
                "items" => array()
            );

            foreach ($content as $key => $value) {

                $tmp = explode(PHP_EOL, $content[$key]['name']);

                $item = array(
                    "price" => array("amount" => (float)round($content[$key]['price'], 2)),
                    "quantity" => (float)round($content[$key]['count'], 3),
                    "tax" => $tax,
                    "text" => strip_tags(htmlspecialchars_decode(MG::textMore($tmp[0], 125)))
                );

                $ym_merchant_receipt["items"][] = $item;
                unset($item);
                unset($tmp);
            }
            if ($data['orderInfo'][$data['id']]['delivery_cost'] > 0) {

                $item = array(
                    "price" => array("amount" => (float)round($data['orderInfo'][$data['id']]['delivery_cost'], 2)),
                    "quantity" => 1,
                    "tax" => $tax,
                    "text" => 'Доставка'
                );
                $ym_merchant_receipt["items"][] = $item;
                unset($item);
            }
            echo "<input name='ym_merchant_receipt' value='".json_encode($ym_merchant_receipt)."' type='hidden' />";
        }
    ?>
